  `vwsd`.`rec_in` AS `rec_in`,
  `vwsd`.`rec_out` AS `rec_out`,
  `vwsd`.`prim_in` AS `prim_in`,
  `vwsd`.`prim_out` AS `prim_out`,
  ((COALESCE(SUM(`vwsd`.`rec_in`) OVER (PARTITION BY `vwsd`.`period_id`, `vwsd`.`currency_id`
  ORDER BY `vwsd`.`trans_day`, `vwsd`.`createdate`), 0.00) - COALESCE(SUM(`vwsd`.`rec_out`) OVER (PARTITION BY `vwsd`.`period_id`, `vwsd`.`currency_id`
  ORDER BY `vwsd`.`trans_day`, `vwsd`.`createdate`), 0.00)) + COALESCE(`tacd_rc`.`amount_closing`, 0.00)) AS `running_rec_net`,
  ((COALESCE(SUM(`vwsd`.`prim_in`) OVER (PARTITION BY `vwsd`.`period_id`, `vwsd`.`currency_id`
  ORDER BY `vwsd`.`trans_day`, `vwsd`.`createdate`), 0.00) - COALESCE(SUM(`vwsd`.`prim_out`) OVER (PARTITION BY `vwsd`.`period_id`, `vwsd`.`currency_id`
  ORDER BY `vwsd`.`trans_day`, `vwsd`.`createdate`), 0.00)) + COALESCE(`tacd_p`.`amount_closing`, 0.00)) AS `running_prim_net`,
  SUM((CASE `vwsd`.`transaction_code_id` WHEN 1 THEN COALESCE(`vwsd`.`rec_in`, `vwsd`.`prim_in`) WHEN 11 THEN `vwsd`.`prim_in` ELSE NULL END)) OVER (PARTITION BY `vwsd`.`period_id`, `vwsd`.`currency_id`
  ORDER BY `vwsd`.`trans_day`, `vwsd`.`createdate`) AS `running_rec_in_ww`,
  SUM((CASE WHEN ((`vwsd`.`transaction_code_id` = 10) AND
      (`vwsd`.`prim_out` IS NOT NULL)) THEN `trftt`.`amount` ELSE NULL END)) OVER (PARTITION BY `vwsd`.`period_id`, `vwsd`.`currency_id`
  ORDER BY `vwsd`.`trans_day`, `vwsd`.`createdate`) AS `running_ww_sent_to_branch`,
  ((((COALESCE(SUM(`vwsd`.`prim_in`) OVER (PARTITION BY `vwsd`.`period_id`, `vwsd`.`currency_id`
  ORDER BY `vwsd`.`trans_day`, `vwsd`.`createdate`), 0.00) - COALESCE(SUM(`vwsd`.`prim_out`) OVER (PARTITION BY `vwsd`.`period_id`, `vwsd`.`currency_id`
  ORDER BY `vwsd`.`trans_day`, `vwsd`.`createdate`), 0.00)) + COALESCE(`tacd_p`.`amount_closing`, 0.00)) - COALESCE(SUM((CASE `vwsd`.`transaction_code_id` WHEN 1 THEN COALESCE(`vwsd`.`rec_in`, `vwsd`.`prim_in`) WHEN 11 THEN `vwsd`.`prim_in` ELSE NULL END)) OVER (PARTITION BY `vwsd`.`period_id`, `vwsd`.`currency_id`
  ORDER BY `vwsd`.`trans_day`, `vwsd`.`createdate`), 0.00)) + COALESCE(SUM((CASE WHEN ((`vwsd`.`transaction_code_id` = 10) AND
      (`vwsd`.`prim_out` IS NOT NULL)) THEN `trftt`.`amount` ELSE NULL END)) OVER (PARTITION BY `vwsd`.`period_id`, `vwsd`.`currency_id`
  ORDER BY `vwsd`.`trans_day`, `vwsd`.`createdate`), 0.00)) AS `running_amnt_for_congr_expense`,
  `vwsd`.`currency_id` AS `currency_id`,
  `vwsd`.`createdate` AS `createdate`,
  `vwsd`.`period_id` AS `period_id`,
  `tp`.`status` AS `status`
FROM (((((`vw_s26_data` `vwsd`
  LEFT JOIN `tbl_period` `tp`
    ON ((`tp`.`tbl_period_id` = `vwsd`.`period_id`)))
  LEFT JOIN `tbl_acc_closing_det` `tacd_rc`
    ON (((`tp`.`previouse_period_id` = `tacd_rc`.`period_id`)
    AND (`vwsd`.`currency_id` = `tacd_rc`.`currency_id`)
    AND (`tacd_rc`.`account_id` = 1))))
  LEFT JOIN `tbl_acc_closing_det` `tacd_p`
    ON (((`tp`.`previouse_period_id` = `tacd_p`.`period_id`)
    AND (`vwsd`.`currency_id` = `tacd_p`.`currency_id`)
    AND (`tacd_p`.`account_id` = 2))))
  LEFT JOIN `tbl_to_62_reference` `ttr`
    ON (((`vwsd`.`period_id` = `ttr`.`period_id`)
    AND (`vwsd`.`currency_id` = `ttr`.`currency_id`))))
  LEFT JOIN `tbl_record_funds_trans_to_62` `trftt`
    ON (((`trftt`.`to_62_reference` = `ttr`.`tbl_to_62_reference_id`)
    AND (`trftt`.`to_62_trans_type_id` = 1)
    AND (`vwsd`.`transaction_code_id` = 10))))
UNION
SELECT
  0 AS `trans_day`,
  'ST' AS `transaction_code`,
  NULL AS `rec_in`,
  NULL AS `rec_out`,
  NULL AS `prim_in`,
  NULL AS `prim_out`,
  `tacd_rec`.`amount_closing` AS `running_rec_net`,
  `tacd_prim`.`amount_closing` AS `running_prim_net`,
  NULL AS `running_rec_in_ww`,
  NULL AS `running_ww_sent_to_branch`,
  `tacd_prim`.`amount_closing` AS `running_amnt_for_congr_expense`,
  `t`.`currency_id` AS `currency_id`,
  `tp`.`createdate` AS `createdate`,
  `tp`.`tbl_period_id` AS `tbl_period_id`,
  `tp`.`status` AS `status`
FROM (((`tbl_period` `tp`
  LEFT JOIN `tblcurrency` `t`
    ON ((1 = 1)))
  LEFT JOIN `tbl_acc_closing_det` `tacd_rec`
