CREATE
DEFINER = 'root'@'localhost'
VIEW vw_cash_box_snap
AS
SELECT
  `tp`.`tbl_period_id` AS `tbl_period_id`,
  `t`.`currency_id` AS `currency_id`,
  `tp`.`status` AS `status`,
  COALESCE(`tacd_prev`.`amount_closing`, 0.00) AS `amount_closing_previouse`,
  COALESCE(`vas`.`net_amount`, 0.00) AS `primary_account_net`,
