  (COALESCE(`vwcpp`.`totalAmount`, 0.00) - COALESCE(`vwcppeo`.`totalAmountOut`, 0.00)) AS `ww_from_contri_box`,
  COALESCE(`trmc`.`amount`, 0.00) AS `resolvedMonContrib`,
  (COALESCE(ABS(`vas`.`income_amount`), 0.00) + COALESCE(ABS(`vas`.`out_amount`), 0.00)) AS `abs_in_plus_out_for_month`,
  COALESCE(`tcd`.`cash_in_box`, 0.00) AS `physically_counted_money_in_cash_box`,
  `tcd`.`cash_in_box` AS `amount_in_cash_box_is_captured`,
  (((COALESCE(`tacd_prev`.`amount_closing`, 0.00) + COALESCE(`vas`.`net_amount`, 0.00)) - COALESCE(`tcd`.`complete_pay_not_recorde`, 0.00)) - COALESCE(`tcd`.`cash_adv_not_clr`, 0.00)) AS `amount_in_cash_box`,
  (((((COALESCE(`tacd_prev`.`amount_closing`, 0.00) + COALESCE(`vas`.`net_amount`, 0.00)) - COALESCE(`tcd`.`complete_pay_not_recorde`, 0.00)) - COALESCE(`tcd`.`cash_adv_not_clr`, 0.00)) - COALESCE(`vwcpp`.`totalAmount`, 0.00)) + COALESCE(`vwcppeo`.`totalAmountOut`, 0.00)) AS `amount_in_cash_box_less_ww`,
  ((((((COALESCE(`tacd_prev`.`amount_closing`, 0.00) + COALESCE(`vas`.`net_amount`, 0.00)) - COALESCE(`tcd`.`complete_pay_not_recorde`, 0.00)) - COALESCE(`tcd`.`cash_adv_not_clr`, 0.00)) - COALESCE(`vwcpp`.`totalAmount`, 0.00)) - COALESCE(`trmc`.`amount`, 0.00)) + COALESCE(`vwcppeo`.`totalAmountOut`, 0.00)) AS `amount_for_congr_use_less_reslov`
FROM ((((((((`tbl_period` `tp`
  LEFT JOIN `tblcurrency` `t`
    ON ((1 = 1)))
  LEFT JOIN `tbl_ledger_s_26` `tls26`
    ON (((`t`.`currency_id` = `tls26`.`currency_id`)
    AND (`tls26`.`period_id` = `tp`.`tbl_period_id`)
    AND (`tls26`.`transaction_code_id` = 10))))
  LEFT JOIN `vw_account_summary` `vas`
    ON (((`vas`.`period_id` = `tp`.`tbl_period_id`)
    AND (`vas`.`account_id` = 2)
    AND (`t`.`currency_id` = `vas`.`currency_id`))))
  LEFT JOIN `tbl_acc_closing_det` `tacd_prev`
    ON (((`tacd_prev`.`period_id` = `tp`.`previouse_period_id`)
    AND (`tacd_prev`.`account_id` = 2)
    AND (`t`.`currency_id` = `tacd_prev`.`currency_id`))))
  LEFT JOIN `tbl_closing_details` `tcd`
    ON (((`tcd`.`period_id` = `tp`.`tbl_period_id`)
    AND (`t`.`currency_id` = `tcd`.`currency_id`))))
  LEFT JOIN `vw_worldwide_contrib_per_period` `vwcpp`
    ON (((`vwcpp`.`period_id` = `tp`.`tbl_period_id`)
    AND (`t`.`currency_id` = `vwcpp`.`currency_id`)
    AND (`tls26`.`tbl_ledger_S_26_id` IS NULL))))
