    AND (`tls26`.`tbl_ledger_S_26_id` IS NULL))))
  LEFT JOIN `vw_worldwide_contrib_per_period_exch_out` `vwcppeo`
    ON (((`vwcppeo`.`period_id` = `tp`.`tbl_period_id`)
    AND (`t`.`currency_id` = `vwcppeo`.`currency_id`))))
WHERE (1 = 1);

