CREATE
DEFINER = 'root'@'localhost'
VIEW vw_error_warning
AS
SELECT
  'TO-62 Error' AS `heading`,
  'Error' AS `type`,
  CONCAT('TO-62 Not Captured For Currency ', '', `t`.`currency`) AS `descrip`
FROM ((((((`vw_cash_box_snap` `vcbs`
  LEFT JOIN `vw_currency_acount_exchange` `vcae`
    ON (((`vcbs`.`currency_id` = `vcae`.`currency_id`)
    AND (`vcae`.`period_id` = `vcbs`.`tbl_period_id`))))
  LEFT JOIN `tblcurrency` `t`
    ON ((`vcbs`.`currency_id` = `t`.`currency_id`)))
  LEFT JOIN `tbl_period` `tp`
    ON ((`vcbs`.`tbl_period_id` = `tp`.`tbl_period_id`)))
  LEFT JOIN `tbl_closing_details` `tcd_prev`
    ON (((`vcbs`.`currency_id` = `tcd_prev`.`currency_id`)
    AND (`tp`.`previouse_period_id` = `tcd_prev`.`period_id`))))
  LEFT JOIN `tbl_mark_for_cary_fwrd_ww` `tbl_cdi`
    ON (((`vcbs`.`currency_id` = `tbl_cdi`.`currency_id`)
    AND (`vcbs`.`tbl_period_id` = `tbl_cdi`.`period_id`))))
  LEFT JOIN `tbl_ledger_s_26` `tls`
    ON (((`tls`.`period_id` = `vcbs`.`tbl_period_id`)
    AND (`t`.`currency_id` = `tls`.`currency_id`)
    AND (`tls`.`transaction_code_id` = 10))))
WHERE ((1 = 1)
AND ((`vcbs`.`ww_from_contri_box` + (CASE WHEN (`tls`.`amount` IS NULL) THEN `tcd_prev`.`ww_cary_fwd` ELSE 0.00 END)) <> 0.00)
AND (`vcbs`.`status` = 'Open')
AND ((`tbl_cdi`.`state` = 'sent')
OR (`tbl_cdi`.`state` IS NULL)))
UNION
SELECT
  'Cash Box System Balancing Error' AS `heading`,
  'Error' AS `type`,
  CONCAT('Cash Box Sys vs Physical Not Balancing For  ', '', `t`.`currency`) AS `descrip`
FROM (`vw_cash_box_snap` `vcbs`
  LEFT JOIN `tblcurrency` `t`
    ON ((`vcbs`.`currency_id` = `t`.`currency_id`)))
WHERE ((1 = 1)
AND ((`vcbs`.`amount_in_cash_box` - `vcbs`.`physically_counted_money_in_cash_box`) <> 0.00)
AND (`vcbs`.`status` = 'Open'))
UNION
SELECT
  'Cash Box System Balancing Error' AS `heading`,
  'Error' AS `type`,
  CONCAT('Cash Box Sys vs Physical Not Balancing For  ', '', `t`.`currency`) AS `descrip`
FROM (`vw_cash_box_snap` `vcbs`
  LEFT JOIN `tblcurrency` `t`
    ON ((`vcbs`.`currency_id` = `t`.`currency_id`)))
WHERE ((1 = 1)
AND ((`vcbs`.`amount_in_cash_box` - `vcbs`.`physically_counted_money_in_cash_box`) = 0.00)
AND (`vcbs`.`abs_in_plus_out_for_month` > 0)
AND (`vcbs`.`amount_in_cash_box_is_captured` IS NULL)
AND (`vcbs`.`status` = 'Open'))
UNION
SELECT
  'Receipts Not Deposited  Error' AS `heading`,
  'Error' AS `type`,
  CONCAT('Receipts Not Deposited For  ', '', `t`.`currency`) AS `descrip`
FROM (`vw_account_standing_p2p` `vasp`
  LEFT JOIN `tblcurrency` `t`
    ON ((`vasp`.`currency_id` = `t`.`currency_id`)))
WHERE ((1 = 1)
AND (`vasp`.`status` = 'Open')
AND (`vasp`.`tbl_account_id` = 1)
AND (`vasp`.`account_net_amount` > 0))
UNION
SELECT
  'TO-62 Warning' AS `heading`,
  'Warning' AS `type`,
  CONCAT('TO-62 Being Carried Forward For ', '', `t`.`currency`) AS `descrip`
FROM (((((`vw_cash_box_snap` `vcbs`
  LEFT JOIN `vw_currency_acount_exchange` `vcae`
    ON (((`vcbs`.`currency_id` = `vcae`.`currency_id`)
    AND (`vcae`.`period_id` = `vcbs`.`tbl_period_id`))))
  LEFT JOIN `tblcurrency` `t`
    ON ((`vcbs`.`currency_id` = `t`.`currency_id`)))
  LEFT JOIN `tbl_period` `tp`
    ON ((`vcbs`.`tbl_period_id` = `tp`.`tbl_period_id`)))
  LEFT JOIN `tbl_closing_details` `tcd_prev`
    ON (((`vcbs`.`currency_id` = `tcd_prev`.`currency_id`)
    AND (`tp`.`previouse_period_id` = `tcd_prev`.`period_id`))))
  LEFT JOIN `tbl_mark_for_cary_fwrd_ww` `tbl_cdi`
    ON (((`vcbs`.`currency_id` = `tbl_cdi`.`currency_id`)
    AND (`vcbs`.`tbl_period_id` = `tbl_cdi`.`period_id`))))
WHERE ((1 = 1)
AND ((`vcbs`.`ww_from_contri_box` + `tcd_prev`.`ww_cary_fwd`) <> 0.00)
AND (`vcbs`.`status` = 'Open')
AND (`tbl_cdi`.`state` = 'fwd'));

